import axios from 'axios';

// Set the base URL for your API
const api = axios.create({
    baseURL: 'http://127.0.0.1:8000/api/',  // Replace with your actual backend URL
});

// Login function
export const loginUser = async (username, password) => {
    try {
        const response = await api.post('token/', {
            username: username,
            password: password,
        });
        return response.data;  // Return the response data (which includes the JWT token)
    } catch (error) {
        console.error('Login failed', error);
        throw error;  // Rethrow the error to be handled by the calling component
    }
};

export default api;  // Export the default axios instance
