// src/api.js

import axios from 'axios';

const API_URL = 'http://127.0.0.1:8000/api/token/'; // Ensure this matches your Django endpoint

export const loginUser = (username, password) => {
    return axios
        .post(API_URL, { username, password }) // Send username and password to backend
        .then((response) => {
            // Store JWT token if login is successful
            localStorage.setItem('token', response.data.access); // Store token in localStorage
            return response.data;
        })
        .catch((error) => {
            // Handle login errors
            throw new Error('Login failed: ' + error.response?.data?.detail || error.message);
        });
};
