import axios from 'axios';

const api = axios.create({
    baseURL: 'http://localhost:8000/api', // Replace with your backend's API base URL
});

// Add a request interceptor to attach the token to every API request
api.interceptors.request.use((config) => {
    const token = localStorage.getItem("token"); // Retrieve token from localStorage
    if (token) {
        config.headers["Authorization"] = `Bearer ${token}`; // Add token to the headers
    }
    return config;
}, (error) => {
    return Promise.reject(error);
});

export default api;
