// Example for App.js to handle logged-out users
const App = () => {
    const isAuthenticated = localStorage.getItem('token');  // Check if the user is authenticated

    return (
        <Router>
            <Routes>
                <Route path="/" element={<LoginForm />} />
                <Route path="/home" element={isAuthenticated ? <HomePage /> : <LoginForm />} />
                <Route path="/add-transaction" element={isAuthenticated ? <AddTransactionForm /> : <LoginForm />} />
            </Routes>
        </Router>
    );
};
