import React, { useEffect, useState } from 'react';
import axios from 'axios';

const FinancialAnalytics = () => {
    const [data, setData] = useState(null); // State to store API response
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        axios
            .get('http://127.0.0.1:8000/api/category-pie-chart/', {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('token')}`, // Ensure token is fetched
                },
            })
            .then((response) => {
                setData(response.data); // Store data in state
                setLoading(false);
            })
            .catch((error) => {
                console.error('Error fetching pie chart:', error); // Debugging error
                setLoading(false);
            });
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h2>Spending by Category (Pie Chart)</h2>
            {data ? (
                <img
                    src="http://127.0.0.1:8000/api/category-pie-chart/"
                    alt="Category Spending Pie Chart"
                />
            ) : (
                <p>Error loading chart.</p>
            )}
        </div>
    );
};

export default FinancialAnalytics;
