// src/api.js

import axios from 'axios';

const API_URL = 'http://127.0.0.1:8000/api/transactions/'; // Adjust if necessary

export const addTransaction = (transactionData) => {
    const token = localStorage.getItem('token'); // Get token from localStorage

    return axios
        .post(API_URL, transactionData, {
            headers: {
                'Authorization': `Bearer ${token}`,  // Send the token in the request headers
            }
        })
        .then((response) => {
            return response;  // Return the response data (for success callback)
        })
        .catch((error) => {
            throw error;  // Handle errors
        });
};
