import React, { useEffect, useState } from 'react';
import axios from 'axios';

const FinancialAnalytics = () => {
    const [data, setData] = useState({});
    const [loading, setLoading] = useState(true);
    const [chart, setChart] = useState(null);

    useEffect(() => {
        // Fetch financial data
        axios.get('http://localhost:8000/api/financial-data/', {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
        })
        .then(response => {
            setData(response.data);
            setLoading(false);
        })
        .catch(error => console.error(error));

        // Fetch pie chart data
        axios.get('http://localhost:8000/api/category-pie-chart/', {
            headers: {
                'Authorization': `Bearer ${localStorage.getItem('token')}`
            }
        })
        .then(response => {
            setChart(`data:image/png;base64,${response.data.image}`);
        })
        .catch(error => console.error(error));
    }, []);

    if (loading) return <div>Loading...</div>;

    return (
        <div>
            <h1>Finance Analytics</h1>
            <h2>Total Spending: {data.total_spending}</h2>
            <h2>Average Spending: {data.average_spending}</h2>
            <h2>Spending by Category:</h2>
            <ul>
                {data.categories.map((cat, index) => (
                    <li key={index}>{cat.category}: {cat.total_amount}</li>
                ))}
            </ul>

            <h2>Spending by Category (Pie Chart)</h2>
            {chart ? <img src={chart} alt="Category Spending Pie Chart" /> : <div>Loading Chart...</div>}
        </div>
    );
};

export default FinancialAnalytics;
