import React, { useState } from 'react';
import api from '../api';
import { useNavigate } from 'react-router-dom';

const AddTransactionForm = () => {
    const [transactionData, setTransactionData] = useState({
        amount: '',
        description: '',
        date: '',
        category: ''
    });
    const navigate = useNavigate();

    // Define the handleInputChange function
    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setTransactionData({ ...transactionData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const token = localStorage.getItem('token');
            if (token) {
                await api.post('/transactions/', transactionData, {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                });
                alert('Transaction added successfully!');
                navigate('/home');
            } else {
                navigate('/login'); // Redirect to login if no token
            }
        } catch (error) {
            console.error('Error adding transaction:', error);
            alert('Failed to add transaction');
        }
    };

    return (
        <div>
            <button onClick={() => navigate('/home')}>Back</button>
            <h2>Add Transaction</h2>
            <form onSubmit={handleSubmit}>
                <input 
                    type="number" 
                    name="amount" 
                    value={transactionData.amount} 
                    onChange={handleInputChange}  // Use handleInputChange here
                    placeholder="Amount"
                    required 
                />
                <input 
                    type="text" 
                    name="description" 
                    value={transactionData.description} 
                    onChange={handleInputChange}  // Use handleInputChange here
                    placeholder="Description"
                    required 
                />
                <input 
                    type="date" 
                    name="date" 
                    value={transactionData.date} 
                    onChange={handleInputChange}  // Use handleInputChange here
                    required 
                />
                  Subjects: <select name="subject" id="subject">
                    <option value="" selected="selected">Groceries</option>
                    <option value="" selected="selected">Entertainment</option>
                    <option value="" selected="selected">Transportation</option>
                    

                </select>



                <div class="dropdown">
                    <button class="dropbtn">Category</button>
                    <div class="dropdown-content">
                        <a href="#">Link 1</a>

                        <a href="#">Link 2</a>
                        <a href="#">Link 3</a>
                    </div>
                </div>
                <input 
                    type="text" 
                    name="category" 
                    value={transactionData.category} 
                    onChange={handleInputChange}  // Use handleInputChange here
                    placeholder="Category"
                    required 
                />
                <button type="submit">Add Transaction</button>
            </form>
        </div>
    );
};

export default AddTransactionForm;
