import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const AddTransactionForm = () => {
  const [description, setDescription] = useState('');
  const [amount, setAmount] = useState('');
  const [category, setCategory] = useState('');
  const [date, setDate] = useState('');
  const [categories] = useState(['Food', 'Entertainment', 'Bills', 'Travel']);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const username = localStorage.getItem('username');
  const token = localStorage.getItem('token');

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!description || !amount || !category || !date) {
      setError('All fields are required');
      return;
    }

    const transactionData = { description, amount, category, date };

    axios
      .post('http://127.0.0.1:8000/api/transactions/', transactionData, {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then(() => {
        alert('Transaction added successfully!');
        setDescription('');
        setAmount('');
        setCategory('');
        setDate('');
      })
      .catch((err) => {
        console.error(err);
        setError('Failed to add transaction.');
      });
  };

  return (
    <div>
      <button onClick={() => navigate('/home')}>
        Back
        <span className="material-icons-outlined">
          backspace
        </span>
      </button>
      <div className="user">
        <span className="material-icons-outlined">account_circle</span>
        <p>{username}</p>
      </div>
      <div className="add-transaction-form">
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <form onSubmit={handleSubmit}>
          <h2 className="heading2">Add a New Transaction</h2>
          <div>
            <input
              placeholder="Description"
              type="text"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
            />
          </div>
          <div>
            <input
              placeholder="Amount"
              type="number"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              required
            />
          </div>
          <div>
            <select
              value={category}
              onChange={(e) => setCategory(e.target.value)}
              required
            >
              <option value="">Select a category</option>
              {categories.map((categoryOption, index) => (
                <option key={index} value={categoryOption}>
                  {categoryOption}
                </option>
              ))}
            </select>
          </div>
          <div>
            <input
              placeholder="Date"
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
            />
          </div>
          <button className="login" type="submit">Add Transaction</button>
        </form>
      </div>
    </div>
  );
};

export default AddTransactionForm;
