import React from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';  // Import Router components
import LoginForm from './components/LoginForm';  // Import LoginForm component
import HomePage from './components/HomePage';  // Import HomePage component
import AddTransactionForm from './components/AddTransactionForm';  // Your Add Transaction form

const App = () => {
    const navigate = useNavigate();  // useNavigate hook to navigate programmatically

    const logout = () => {
        localStorage.removeItem('token');  // Remove the token
        localStorage.removeItem('username');  // Remove the username
        navigate('/');  // Redirect to login page after logout
    };

    const isAuthenticated = localStorage.getItem('token');  // Check if user is authenticated

    return (
        <Router>
            <Routes>
                <Route path="/" element={<LoginForm />} />  {/* Default route shows the login form */}
                <Route path="/home" element={isAuthenticated ? <HomePage logout={logout} /> : <LoginForm />} />  {/* Home page after login */}
                <Route path="/add-transaction" element={isAuthenticated ? <AddTransactionForm /> : <LoginForm />} />  {/* Add Transaction page */}
            </Routes>
        </Router>
    );
};

export default App;
