import React, { useState } from 'react';
import api, { loginUser } from '../api';  // Import the default export and loginUser if you have it

const LoginForm = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await api.post('/token/', {  // Login API request
                username,
                password
            });
            alert('Login successful!');
            console.log('JWT Token:', response.data.access);  // Optional: log the token for debugging
            localStorage.setItem('token', response.data.access);  // Save the token to localStorage
            localStorage.setItem('username', username);  // Save the username to localStorage
            navigate('/home');  // Redirect to home page
        } catch (error) {
            console.error('Error logging in', error);
            alert('Login failed');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
            />
            <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
            />
            <button type="submit">Login</button>
        </form>
    );
};

export default LoginForm;
