// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';  // Import Router components
import LoginForm from './components/LoginForm';  // Import LoginForm component
import HomePage from './components/HomePage';  // Import HomePage component
import AddTransactionForm from './components/AddTransactionForm';  // Your Add Transaction form

const App = () => {
    return (
        <Router>
            <Routes>
                <Route path="/" element={<LoginForm />} />  {/* Default route shows the login form */}
                <Route path="/home" element={<HomePage />} />  {/* Home page after login */}
                <Route path="/add-transaction" element={<AddTransactionForm />} />  {/* Add Transaction page */}
            </Routes>
        </Router>
    );
};
// Example in HomePage or Navbar to logout the user
const logout = () => {
    localStorage.removeItem('token');  // Remove the token
    localStorage.removeItem('username');  // Remove the username
    navigate('/');  // Redirect to login page
};


export default App;
